import React from 'react';
import CloseButtonPopup from './CloseButtonPopup.js';


function ImagePopup(props) {

    const [openPopup, setOpenPopup] = React.useState(props.onClose);
    function handleOpenPopup() {
        setOpenPopup(!openPopup)
    }

    return (
        <div className={`popup popup_image ${openPopup ? 'popup_opened' : ''}`}>
            <div className="popup__container-image">
                <CloseButtonPopup isClose={handleOpenPopup} />
                    <img className="popup__img" src={props.card.link} title='' alt='' />
                <p className="popup__signature"></p>
            </div>
        </div>
    )
}

export default ImagePopup;
