import iconEditPath from './../images/edit.svg';
import iconAddPath from './../images/add.svg';

function Main () {

   function handleEditAvatarClick(){
       document.querySelector('.popup_upload-avatar').classList.add('popup_opened');
   }
   function handleEditProfileClick() {}
   function handleAddPlaceClick() {}

    return (
        <main className="content">
            <section className="profile">
                <div className="profile__info" onClick={handleEditAvatarClick}>
                <div className="profile__avatar-hover">
                    <div className="profile__avatar" alt="фотография Жак-Ив Кусто" /></div>
                <div className="profile__text">
                    <div className="profile__title"><h1 className="profile__name">Жак-Ив Кусто</h1>
                    <button type="button" className="profile__edit-button" onClick={handleEditProfileClick}><img src={iconEditPath} alt="кнопка редактирования" />
                    </button>
                    </div>
                    <p className="profile__subtitle">Исследователь океана</p>
                </div>
                </div>
                <button type="button" className="profile__add-button" onClick={handleAddPlaceClick}><img className="profile__icon-add" src={iconAddPath} title="кнопка добавить" alt="кнопка добавить" />
                </button>
            </section>
            <section className="elements">
            </section>
        </main>
    );

}



export default Main;
