import React from 'react';
import { CurrentUserContext } from '../contexts/CurrentUserContext';

function Card (props) {

    const currentUser = React.useContext(CurrentUserContext);

    // Определяем, являемся ли мы владельцем текущей карточки
    const isOwn = props.card.owner._id === currentUser._id;

// Создаём переменную, которую после зададим в `className` для кнопки удаления
    const cardDeleteButtonClassName = (
    `element__delete-button ${isOwn ? 'element__delete-button_visible' : 'element__delete-button_hidden'}`
    );

// Определяем, есть ли у карточки лайк, поставленный текущим пользователем
const isLiked = props.card.likes.some(i => i._id === currentUser._id);

// Создаём переменную, которую после зададим в `className` для кнопки лайка
const cardLikeButtonClassName = `element__button-like-counter ${isLiked ? 'element__button-like-counter_visible' : 'element__button-like-counter_hidden'}`; 

    function handleClick() {
        props.onCardClick(props.card);
      } 

    return (
        <div className="card__template">
            <article className="element">
                <img className="element__photo" src={props.card.link} title={`Пользователь ${props.card.owner.name} загрузил фото и назвал ${props.card.name}`} alt={`Пользователь с ником ${props.card.owner.name}, загрузил фото и назвал: ${props.card.name}`} onClick={handleClick} />
                <button type="button" className={cardDeleteButtonClassName} onClick={props.onDeleteCard}></button>
                <div className="element__down-part">
                <h2 className="element__name">{props.card.name}</h2>
                    <div className="element__like">
                        <button type="button" className="element__button-like"></button>
                          <span className={cardLikeButtonClassName}>{props.card.likes.length}</span> 
                    </div>
                </div>
            </article>
        </div>
        );
}

export default Card;
